mapper.select:
select count(*)
  from review
 where review_receiver = #{userEmail}

select count(*)
  from review
 where review_sender = #{userEmail}

mapper.update:
select distinct ifnull(sum(review_score), 0) from review where review_receiver = #{reviewReceiver}
update user set
user_review_cnt = user_review_cnt + 1, user_score = #{reviewScore} / user_review_cnt where user_email = #{reviewReceiver}
user_review_cnt = user_review_cnt - 1, user_score = case when #{reviewScore} = 0 then 0 else #{reviewScore} / user_review_cnt end where user_email = #{reviewReceiver}
user_score = case when #{reviewScore} = 0 then 0 else #{reviewScore} / user_review_cnt end where user_email = #{reviewReceiver}

mapper.insert:
insert into review (
review_title, review_content, deal_no, review_sender, review_receiver, review_score
) values (
	#{reviewTitle},
	#{reviewContent},
	#{dealNo},
	#{reviewSender},
	#{reviewReceiver},
	#{reviewScore}
)

mapper.update:
update review	
   set review_score = #{reviewScore},
   	   review_title = #{reviewTitle},
       review_content = #{reviewContent}
 where review_no = #{reviewNo}
