mapper.select:
select * 
  from user
 where user_email = #{userEmail}
   and user_pass = #{userPass}

select user_pass
from user
where user_email = #{email}

mapper.update:
update user 
   set user_email = ${userEmail},
   	   user_pass = ${userPass},
   	   user_nickname = ${userNickname}
 where user_phnum = ${userPhnum}
